import * as React from 'react';
import { StyleSheet } from 'react-native';

import EditScreenInfo from '../components/EditScreenInfo';
import { Text, View } from '../components/Themed';

import { Image, Button } from "react-native";

import * as Speech from 'expo-speech';

import { TOPOKANJI } from '../assets/kanji/topokanji.tsx';

//const JishoApi = require('unofficial-jisho-api');
//const jisho = new JishoApi();

const tryRequire = (path) => {
  try {
   return require(`${path}`);
  } catch (err) {
   return null;
  }
};

export default function TabTwoScreen() {
    let kanji_index = 0;
    let current_kanji = TOPOKANJI[kanji_index];
    let image = tryRequire("../assets/card_images/" + current_kanji);
    console.log(current_kanji)
    return (
        <View>
        <Image
        source={image ? image : require("../assets/card_images/missing")}
        style={{
                height: '100%',
                width:'100%',
                alignSelf:'center'
        }}
        />
        <Text>{current_kanji[0]}</Text>
        <Text style={{ color: "#777", paddingTop: 5 }}>
        {current_kanji[kanji_index]}
        </Text>
        <Button title="聴く" onPress={() => Speech.speak(current_kanji, {language: "ja-JP", rate: 0.7})} />
        <Button title="分かります" onPress={() => Speech.speak(current_kanji, {language: "ja-JP", rate: 0.7})} />
        <Button title="分かりません" onPress={() => Speech.speak(current_kanji, {language: "ja-JP", rate: 0.7})} />
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        fontSize: 20,
        fontWeight: 'bold',
    },
    separator: {
        marginVertical: 30,
        height: 1,
        width: '80%',
    },
});
